document.addEventListener("click",t=>{let e=t.target.classList.contains("gspb-product-quick-view-box-button")?t.target:t.target.closest(".gspb-product-quick-view-box-button");if(e){t.preventDefault(),e.parentNode.parentNode.querySelector(".gspb-product-quick-view-popup").classList.add("active");let s=e.closest(".gspbgrid_item");s&&s.classList.add("quickviewitem")}t.target.matches(".gspb-product-quick-view-close")&&(t.preventDefault(),t.target.parentNode.parentNode.classList.remove("active"))});